import queue
from urllib3.connection import HTTPConnection, HTTPSConnection
from urllib3.util import Timeout, Retry

class HTTPConnectionPool:
    def __init__(self, host, port=None, strict=False, timeout=Timeout.DEFAULT_TIMEOUT, maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, **conn_kw):
        self.scheme = 'http'
        self.host = host
        self.port = port
        self.timeout = timeout
        self.retries = retries
        self.pool = queue.LifoQueue(maxsize=maxsize)
        self.block = block
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers
        self.headers = headers or {}
        self.conn_kw = conn_kw

    def _new_conn(self):
        conn = HTTPConnection(self.host, self.port, timeout=self.timeout.connect, strict=self.block, **self.conn_kw)
        if self.proxy:
            conn.set_tunnel(self.proxy, headers=self.proxy_headers)
        return conn

    def _get_conn(self, timeout=None):
        try:
            return self.pool.get(block=self.block, timeout=timeout)
        except queue.Empty:
            raise

    def _put_conn(self, conn):
        try:
            self.pool.put(conn, block=self.block)
        except queue.Full:
            conn.close()

    def _make_request(self, conn, method, url, timeout=Timeout.DEFAULT_TIMEOUT, **httplib_request_kw):
        conn.request(method, url, **httplib_request_kw)
        return conn.getresponse()

    def urlopen(self, method, url, body=None, headers=None, retries=None, redirect=True, assert_same_host=True, timeout=Timeout.DEFAULT_TIMEOUT, pool_timeout=None, release_conn=None, **response_kw):
        conn = self._get_conn(timeout=pool_timeout)
        try:
            merged_headers = {**self.headers, **(headers or {})}
            response = self._make_request(conn, method, url, timeout=timeout, body=body, headers=merged_headers)
            return response
        except Exception as e:
            if retries and retries.total > 0:
                retries.increment()
                return self.urlopen(method, url, body, headers, retries=retries, **response_kw)
            raise
        finally:
            if release_conn:
                self._put_conn(conn)

    def close(self):
        while not self.pool.empty():
            conn = self.pool.get_nowait()
            conn.close()

class HTTPSConnectionPool(HTTPConnectionPool):
    def __init__(self, host, port=None, strict=False, timeout=Timeout.DEFAULT_TIMEOUT, maxsize=1, block=False, headers=None, retries=None, _proxy=None, _proxy_headers=None, key_file=None, cert_file=None, cert_reqs='required', ca_certs=None, ssl_version=None, assert_hostname=None, assert_fingerprint=None, **conn_kw):
        super().__init__(host, port, strict, timeout, maxsize, block, headers, retries, _proxy, _proxy_headers, **conn_kw)
        self.scheme = 'https'
        self.key_file = key_file
        self.cert_file = cert_file
        self.cert_reqs = cert_reqs
        self.ca_certs = ca_certs
        self.ssl_version = ssl_version
        self.assert_hostname = assert_hostname
        self.assert_fingerprint = assert_fingerprint

    def _prepare_conn(self, conn):
        conn.key_file = self.key_file
        conn.cert_file = self.cert_file
        conn.cert_reqs = self.cert_reqs
        conn.ca_certs = self.ca_certs
        conn.ssl_version = self.ssl_version
        conn.assert_hostname = self.assert_hostname
        conn.assert_fingerprint = self.assert_fingerprint
        return conn

    def _new_conn(self):
        conn = HTTPSConnection(self.host, self.port, timeout=self.timeout.connect, strict=self.block, **self.conn_kw)
        if self.proxy:
            conn.set_tunnel(self.proxy, headers=self.proxy_headers)
        return self._prepare_conn(conn)

