from sqlalchemy import Column, Date, String, JSON
from datetime import date
from typing import Optional, Dict, Any

class ExchangeRateAPI:
    def __init__(self):
        self.app = Sanic("ExchangeRateAPI")
        self.db = Gino()
        self.config = {"DATABASE_URL": os.environ.get("DATABASE_URL")}
        self.db.init_app(self.app, self.config["DATABASE_URL"])
        self._setup_routes()

    def _setup_routes(self):
        self.app.route("/exchange-rates", methods=["GET"])(self.exchange_rates)

    async def exchange_rates(self, request):
        date_param = request.args.get("date")
        base = request.args.get("base", "EUR")
        symbols = request.args.get("symbols")

        if date_param:
            try:
                query_date = date.fromisoformat(date_param)
            except ValueError:
                return json({"error": "Invalid date format"}, status=400)
        else:
            latest = await ExchangeRates.query.order_by(ExchangeRates.date.desc()).gino.first()
            if not latest:
                return json({"error": "No data available"}, status=404)
            query_date = latest.date

        rate_entry = await ExchangeRates.query.where(
            (ExchangeRates.date == query_date) & (ExchangeRates.base == base)
        ).gino.first()

        if not rate_entry:
            return json({"error": "No exchange rates found for the specified parameters"}, status=404)

        response = {
            "base": base,
            "date": query_date.isoformat(),
            "rates": rate_entry.rates
        }

        if symbols:
            filtered_rates = {}
            for symbol in symbols.split(","):
                if symbol in response["rates"]:
                    filtered_rates[symbol] = response["rates"][symbol]
            response["rates"] = filtered_rates

        return json(response)

    def run(self, host="0.0.0.0", port=8000):
        self.app.run(host=host, port=port)

class ExchangeRates(Gino.Model):
    __tablename__ = 'exchange_rates'

    date = Column(Date, primary_key=True)
    base = Column(String, primary_key=True)
    rates = Column(JSON)
