from sanic import Sanic
from sanic.request import Request
from sanic.response import HTTPResponse
from typing import Optional, Dict, Any, Literal, Union, Tuple, List
from io import BytesIO
import json
import asyncio

class SanicHttpClient:
    def __init__(self, graphiql: Optional[bool] = False, graphql_ide: Optional[str] = "graphiql", allow_queries_via_get: bool = False, result_override: Optional[Any] = None, multipart_uploads_enabled: bool = False):
        self.app = Sanic("SanicHttpClient")
        self.graphiql = graphiql
        self.graphql_ide = graphql_ide
        self.allow_queries_via_get = allow_queries_via_get
        self.result_override = result_override
        self.multipart_uploads_enabled = multipart_uploads_enabled
        self._setup_routes()

    def _setup_routes(self):
        @self.app.route("/graphql", methods=["GET", "POST"])
        async def graphql_handler(request: Request):
            if self.result_override is not None:
                return HTTPResponse(json.dumps(self.result_override), status=200, content_type="application/json")
            if request.method == "GET" and self.allow_queries_via_get:
                query = request.args.get("query")
                variables = request.args.get("variables")
                return HTTPResponse(json.dumps({"data": query}), status=200, content_type="application/json")
            elif request.method == "POST":
                if request.headers.get("Content-Type", "").startswith("multipart/form-data"):
                    form = await request.form()
                    query = form.get("query")
                    variables = form.get("variables")
                    return HTTPResponse(json.dumps({"data": query}), status=200, content_type="application/json")
                else:
                    data = await request.json
                    return HTTPResponse(json.dumps({"data": data.get("query")}), status=200, content_type="application/json")
            return HTTPResponse(json.dumps({"error": "Unsupported method"}), status=405, content_type="application/json")

    async def _graphql_request(self, method: Literal["get", "post"], query: Optional[str] = None, variables: Optional[Dict[str, object]] = None, files: Optional[Dict[str, BytesIO]] = None, headers: Optional[Dict[str, str]] = None, **kwargs: Any) -> HTTPResponse:
        if method == "get" and self.allow_queries_via_get:
            url = "/graphql?query={}".format(query or "")
            if variables:
                url += "&variables={}".format(json.dumps(variables))
            return await self.app.test_client.get(url, headers=headers)
        else:
            payload = {"query": query, "variables": variables}
            if files:
                form_data = {"query": query, "variables": json.dumps(variables)}
                for name, file in files.items():
                    form_data[name] = (file.name, file.read(), "application/octet-stream")
                return await self.app.test_client.post("/graphql", headers=headers, data=form_data)
            else:
                return await self.app.test_client.post("/graphql", headers=headers, json=payload)

    async def request(self, url: str, method: Literal["get", "post", "patch", "put", "delete"], headers: Optional[Dict[str, str]] = None) -> HTTPResponse:
        if method == "get":
            return await self.app.test_client.get(url, headers=headers)
        elif method == "post":
            return await self.app.test_client.post(url, headers=headers)
        elif method == "patch":
            return await self.app.test_client.patch(url, headers=headers)
        elif method == "put":
            return await self.app.test_client.put(url, headers=headers)
        elif method == "delete":
            return await self.app.test_client.delete(url, headers=headers)
        else:
            raise ValueError("Unsupported HTTP method")

    async def get(self, url: str, headers: Optional[Dict[str, str]] = None) -> HTTPResponse:
        return await self.request(url, "get", headers)

    async def post(self, url: str, data: Optional[bytes] = None, json: Optional[Dict] = None, headers: Optional[Dict[str, str]] = None) -> HTTPResponse:
        return await self.app.test_client.post(url, data=data, json=json, headers=headers)
