    def _read_topic_words_from_file(self, topic_words_file_name='topic_words.lda.txt'):
        topic_words = defaultdict(list)
        file_path = os.path.join(self.model_dir, topic_words_file_name)
        if not os.path.exists(file_path):
            return topic_words
        with open(file_path, 'r') as f:
            line = f.readline()
            while line:
                pos = line.find('=')
                line = line[pos + 2:]
                topic_id, num = line.strip().split('\t')
                topic_id, num = int(topic_id), int(num)
                f.readline()
                items = list()
                for i in range(num):
                    data = f.readline()
                    word, score = data.strip().split('\t')
                    items.append([word, float(score)])
                topic_words[topic_id] = items
                line = f.readline()
        return topic_words

