        param_value = (request.form.getlist(param_name) if is_list else request.form.getlist(param_name) or \
                     request.args.get(param_name) or \
                     default_value
        if param_value is None:
            return param_value
        value_type = type(param_value)
        if is_list:
            return param_value if value_type == list else [param_value]
        return param_value[0] if value_type == list else param_value

    def _strip_to_none(self, text: str):
        if text is None:
            return None
        text = text.strip()
        text = re.sub(RE_BACKSPACES, '', text)
        if len(text) == 0:
            return None
        if text == 'None':
            return None
        return text

    def _response(self, success: bool = True, data=None, message=None):
        data = {'success': success, 'message': message, 'data': data}
        data = {k: v for k, v in data.items() if v is not None}
        try:
            return json(data, ensure_ascii=False)
        except Exception:
            data = {'success': success, 'message': traceback.format_exc()}
            return json(data, ensure_ascii=False)

    def _error_response(self, message='Invalid request'):
        return self._response(success=False, message=message)

    def _setup_routes(self):
