            try:
                text = self._get_param(request, 'text')
                if text is None:
                    return self._error_response()
                result = self.inference_engine_lda.tokenize(text)
                return self._response(data=result)
            except Exception as err:
                return self._error_response(str(err))

        @self.app.route('/lda', methods=["POST"])
        @doc.summary("LDA模型推断")
        async def api_lda(request):
            try:
                text = self._get_param(request, 'text')
                n = int(self._get_param(request, 'n', 10))
                if text is None:
                    return self._error_response()
                words = self.inference_engine_lda.tokenize(text)
                result = self.inference_engine_lda.lda_infer(words)
                result = result[:n]
                result = [
                    {
                        'topic_id': topic_id,
                        'score': score,
                        'topic_words': self.twe.nearest_words_around_topic(topic_id),
                        'topic_words_poly': self.lda_topic_words.get(topic_id),
                    } for topic_id, score in result
                ]
                return self._response(data=result)
            except Exception as err:
                return self._error_response(str(err))

    def run(self):
        self.app.run(host='0.0.0.0', port=5000, workers=self.n_workers)

