import queue
import http.client
from typing import Any, Mapping, Optional, TypeVar, Generic, Callable, Union, Type
from http.client import HTTPConnection, BaseHTTPResponse
from urllib.parse import urlparse

T = TypeVar('T')

class HTTPConnectionPool:
    def __init__(self, host: str, port: int | None = None, timeout: Union[float, None] = None, maxsize: int = 1, block: bool = False, headers: Mapping[str, str] | None = None, retries: Union[None, int, bool] = None, _proxy: str | None = None, _proxy_headers: Mapping[str, str] | None = None, **conn_kw: Any) -> None:
        self.host = host
        self.port = port
        self.timeout = timeout
        self.maxsize = maxsize
        self.block = block
        self.headers = headers
        self.retries = retries
        self.proxy = _proxy
        self.proxy_headers = _proxy_headers
        self.pool = queue.LifoQueue(maxsize=maxsize)
        self.num_connections = 0
        self.num_requests = 0
        self.conn_kw = conn_kw

    def _new_conn(self) -> HTTPConnection:
        conn = http.client.HTTPConnection(
            host=self.host,
            port=self.port,
            timeout=self.timeout,
            **self.conn_kw
        )
        if self.headers:
            for k, v in self.headers.items():
                conn.putheader(k, v)
        return conn

    def _get_conn(self, timeout: float | None = None) -> HTTPConnection:
        try:
            conn = self.pool.get(timeout=timeout)
            self.num_requests += 1
            return conn
        except queue.Empty:
            if self.num_connections < self.maxsize:
                self.num_connections += 1
                return self._new_conn()
            else:
                raise ConnectionError("Pool is full")

    def _put_conn(self, conn: HTTPConnection | None) -> None:
        if conn is None:
            return
        try:
            self.pool.put(conn, block=self.block)
        except queue.Full:
            pass

    def _make_request(self, conn: HTTPConnection, method: str, url: str, timeout: Union[float, None] = None, body: Any = None, headers: Mapping[str, str] | None = None, **response_kw: Any) -> BaseHTTPResponse:
        if headers is None:
            headers = {}
        if self.headers:
            headers.update(self.headers)
        conn.request(method, url, body=body, headers=headers, timeout=timeout)
        return conn.getresponse()

    def urlopen(self, method: str, url: str, body: Any = None, headers: Mapping[str, str] | None = None, retries: Union[None, int, bool] = None, timeout: Union[float, None] = None, **response_kw: Any) -> BaseHTTPResponse:
        if retries is None:
            retries = self.retries
        conn = self._get_conn(timeout=timeout)
        try:
            response = self._make_request(conn, method, url, timeout=timeout, body=body, headers=headers, **response_kw)
            return response
        except Exception as e:
            if retries > 0:
                return self.urlopen(method, url, body, headers, retries=retries - 1, timeout=timeout, **response_kw)
            else:
                raise

    def close(self) -> None:
        self._close_pool_connections(self.pool)

    @staticmethod
    def _close_pool_connections(pool: queue.LifoQueue) -> None:
        while True:
            try:
                conn = pool.get_nowait()
                conn.close()
            except queue.Empty:
                break
