import http.client
import json
import uuid
import re
import ssl
from urllib.parse import urlparse
from typing import Any, List, Optional, Tuple, Callable

class JSONRPCExecutor:
    def __init__(self, command: str, url: str, jsonrpc_method: str, jsonrpc_params: Optional[List[Any]] = None, status: str = "^2\\d\\d$", result_validator: Callable[[Any], Tuple[bool, Optional[str]]] = None, verify_tls: bool = True, method: str = "POST"):
        self.command = command
        self.url = url
        self.jsonrpc_method = jsonrpc_method
        self.jsonrpc_params = jsonrpc_params or []
        self.status = status
        self.result_validator = result_validator
        self.verify_tls = verify_tls
        self.method = method

    def _send_request(self, conn: http.client.HTTPConnection, path: str) -> None:
        request_data = {
            "jsonrpc": "2.0",
            "method": self.jsonrpc_method,
            "params": self.jsonrpc_params,
            "id": str(uuid.uuid4())
        }
        json_data = json.dumps(request_data)
        conn.request(self.method, path, body=json_data, headers={"Content-Type": "application/json"})

    def _validate_response(self, response: http.client.HTTPResponse) -> bool:
        return re.match(self.status, str(response.status)) is not None

    def execute(self) -> bool:
        parsed_url = urlparse(self.url)
        host = parsed_url.hostname
        port = parsed_url.port
        if not port:
            port = 443 if parsed_url.scheme == 'https' else 80
        path = parsed_url.path
        if parsed_url.query:
            path += '?' + parsed_url.query

        try:
            if parsed_url.scheme == 'https':
                if self.verify_tls:
                    conn = http.client.HTTPSConnection(host, port)
                else:
                    context = ssl._create_unverified_context()
                    conn = http.client.HTTPSConnection(host, port, context=context)
            else:
                conn = http.client.HTTPConnection(host, port)

            self._send_request(conn, path)
            response = conn.getresponse()

            if not self._validate_response(response):
                return False

            response_data = response.read().decode('utf-8')
            try:
                response_json = json.loads(response_data)
            except json.JSONDecodeError:
                return False

            if not isinstance(response_json, dict):
                return False
            if response_json.get('jsonrpc') != '2.0':
                return False
            if 'error' in response_json:
                return False
            if 'result' not in response_json:
                return False

            if self.result_validator:
                success, _ = self.result_validator(response_json['result'])
                if not success:
                    return False

            return True

        except Exception:
            return False
        finally:
            conn.close()
