        self.__service_url = service_url
        self.__url = urllib.parse.urlparse(service_url) if service_url else None
        self.__id_count = 0
        self.__auth_header = None
        self.__conn = connection

        if not self.__conn:
            if not self.__url:
                raise ValueError("Service URL must be provided if no connection is given")
            if self.__url.scheme == 'https':
                self.__conn = http.client.HTTPSConnection(self.__url.netloc, timeout=timeout)
            else:
                self.__conn = http.client.HTTPConnection(self.__url.netloc, timeout=timeout)

        if self.__url and self.__url.username and self.__url.password:
            auth = f"{self.__url.username}:{self.__url.password}"
            self.__auth_header = base64.b64encode(auth.encode()).decode()

    def _call(self, service_name, *args):
        self.__id_count += 1
        request = {
            "jsonrpc": "2.0",
            "method": service_name,
            "params": args,
            "id": self.__id_count
        }
        headers = {}
        if self.__auth_header:
            headers['Authorization'] = f"Basic {self.__auth_header}"
        self.__conn.request("POST", self.__url.path, json.dumps(request), headers)
        response = self._get_response()
        return response['result']

    def _get_response(self):
        response = self.__conn.getresponse()
        if response.status != 200:
            raise Exception(f"HTTP error {response.status}: {response.reason}")
        data = response.read().decode()
        try:
            result = json.loads(data)
        except json.JSONDecodeError:
            raise Exception("Invalid JSON response")
        if 'error' in result:
            raise Exception(f"JSON-RPC error: {result['error']}")
        return result

    def close(self):
        if self.__conn:
            self.__conn.close()

    def __del__(self):
        self.close()

