        data['source_lang'] = sourceLang
    if numberAlternative > 0:
        data['number_alternatives'] = numberAlternative
    try:
        with httpx.Client(proxies=proxies) as client:
            response = client.post(url, data=data, headers=headers)
    except httpx.RequestError:
        return None
    if response.status_code != 200:
        if response.status_code == 429:
            raise TooManyRequestsException("Too many requests")
        return None
    response_json = response.json()
    translations = response_json.get('translations', [])
    if not translations:
        return None
    main_translation = translations[0]
    if numberAlternative <= 1:
        result = main_translation['text']
    else:
        alternatives = [alt['text'] for alt in main_translation.get('alternatives', [])]
        result = alternatives
    if printResult:
        print(result)
    return result
