        return command_id, sequence_id, body

    def close(self) -> None:
        if self.__socket:
            try:
                self.send(CMPPTerminate())
            finally:
                self.__socket.close()
                self._is_connect = False

    def _cmpp_connect(self) -> None:
        connect_msg = CMPPConnect(self.sp_id, self.sp_secret, self.src_id)
        self.send(connect_msg)
        resp = self.recv()
        if resp[0] != 0x80000001 or resp[1] != connect_msg.sequence_id:
            raise RuntimeError("CMPP connect failed")

    def _cmpp_send_sms(self, dest: List[str], sign_name: str, template_code: str, template_param: Dict) -> None:
        submit_msg = CMPPSubmit(dest, sign_name, template_code, template_param, self.src_id, self.service_id)
        self.send(submit_msg)

    def send_sms(self, dest: List[str], sign_name: str, template_code: str, template_param: Dict) -> None:
        try:
            self._connect()
            self._cmpp_send_sms(dest, sign_name, template_code, template_param)
        except Exception as e:
            raise RuntimeError(f"Failed to send SMS: {str(e)}") from e

class CMPPBaseRequestInstance:
    def __init__(self, command_id: int, sequence_id: int):
        self.command_id = command_id
        self.sequence_id = sequence_id

    def serialize(self) -> bytes:
        header = struct.pack('!III', self.command_id, 0, self.sequence_id)
        length = len(header) + len(self.body())
        header = struct.pack('!III', self.command_id, 0, self.sequence_id)
        header = header[:4] + struct.pack('!I', length) + header[8:]
        return header + self.body()

    def body(self) -> bytes:
        raise NotImplementedError

class CMPPConnect(CMPPBaseRequestInstance):
    def __init__(self, sp_id: str, sp_secret: str, src_id: str):
        super().__init__(0x00000000, 0)
        self.sp_id = sp_id
        self.sp_secret = sp_secret
        self.src_id = src_id

    def body(self) -> bytes:
        return self.sp_id.encode('utf-8') + b'\x00' + self.sp_secret.encode('utf-8') + b'\x00' + self.src_id.encode('utf-8') + b'\x00'

class CMPPSubmit(CMPPBaseRequestInstance):
    def __init__(self, dest: List[str], sign_name: str, template_code: str, template_param: Dict, src_id: str, service_id: str):
        super().__init__(0x00000002, 0)
        self.dest = dest
        self.sign_name = sign_name
        self.template_code = template_code
        self.template_param = template_param
        self.src_id = src_id
        self.service_id = service_id

    def body(self) -> bytes:
        dest_str = ''.join(self.dest)
        param = f'{{"code":"{self.template_code}","data":{self.template_param}}}'.encode('utf-8')
        return dest_str.encode('utf-8') + b'\x00' + self.sign_name.encode('utf-8') + b'\x00' + self.service_id.encode('utf-8') + b'\x00' + param + b'\x00'

class CMPPTerminate(CMPPBaseRequestInstance):
    def __init__(self):
        super().__init__(0x00000003, 0)

    def body(self) -> bytes:
        return b''
