        json_str = json.dumps(dictionary)
        self._send_string(json_str)

    def _get_json_dict(self) -> Dict[str, Any]:
        data = self._get_data()
        if data is None:
            return {}
        return json.loads(data)

    def _get_data(self) -> Optional[str]:
        length_str = b''
        while len(length_str) < 10:
            chunk = self.connection.recv(10 - len(length_str))
            if not chunk:
                return None
            length_str += chunk
        length = int(length_str.strip())
        data = b''
        while len(data) < length:
            packet = self.connection.recv(length - len(data))
            if not packet:
                return None
            data += packet
        return data.decode()

    def _send_string(self, string: str) -> None:
        length = len(string)
        self.connection.sendall(f"{length:10}".encode())
        self.connection.sendall(string.encode())

    def close(self) -> None:
        if self.connection:
            self.connection.close()

