            if not data:
                break
            for line in data.split('\n'):
                line = line.strip()
                if not line:
                    continue
                parts = line.split()
                if not parts:
                    continue
                command = parts[0]
                args = parts[1:]
                parsed_args = {}
                for arg in args:
                    if '=' in arg:
                        key, value = arg.split('=', 1)
                        parsed_args[key] = value
                success = handler(command, parsed_args)
                if success:
