                    break
                self.buffer += data.decode('utf-8')
                if self.buffer.endswith("QUIT"):
                    self.send_in_chunks(client_sock)
                    break
                if self.start_resend_event.is_set():
                    self.send_in_chunks(client_sock)
                    self.start_resend_event.clear()
            client_sock.sendall(b"QUIT acknowledged")
        finally:
            client_sock.close()

    def send_in_chunks(self, client_sock):
