        if sock in self.input_list:
            self.input_list.remove(sock)
        if sock in self.channel:
            remote_sock = self.channel[sock]
            del self.channel[sock]
            remote_sock.close()
        else:
            for client, remote in list(self.channel.items()):
                if remote == sock:
                    del self.channel[client]
                    client.close()
                    break
        sock.close()

