import threading

class TCPRequestHandler(socketserver.BaseRequestHandler):
    def get_data(self):
        size_data = self.request.recv(2)
        if len(size_data) < 2:
            raise ConnectionError("Incomplete size data")
        size = int.from_bytes(size_data, 'big')
        data = b''
        while len(data) < size:
            chunk = self.request.recv(size - len(data))
            if not chunk:
                raise ConnectionError("Connection closed")
            data += chunk
        return data

    def send_data(self, data):
        size = len(data)
        if size > 65535:
            raise ValueError("Data too large for 2-byte size field")
        size_bytes = size.to_bytes(2, 'big')
        self.request.sendall(size_bytes + data)

    def handle(self):
        try:
            data = self.get_data()
            response = b'Processed: ' + data
            self.send_data(response)
        except Exception as e:
            self.send_data(f"Error: {str(e)}".encode())

