    def _split_text_into_chunks(self, text: str) -> List[str]:
        max_bytes = 4096
        chunks = []
        current_chunk = ""
        for line in text.split("\n"):
            if len(current_chunk.encode("utf-8")) + len(line.encode("utf-8")) > max_bytes:
