from typing import Dict, List, Callable, Optional

class _DataStream:
    def __init__(self, endpoint: str, key_id: str, secret_key: str, raw_data: bool = False, websocket_params: Optional[Dict] = None) -> None:
        self._endpoint = endpoint
        self._key_id = key_id
        self._secret_key = secret_key
        self._ws = None
        self._running = False
        self._loop = asyncio.get_event_loop()
        self._raw_data = raw_data
        self._stop_stream_queue = queue.Queue()
        self._handlers = {
            'trades': {},
            'quotes': {},
            'bars': {},
            'updated_bars': {},
            'daily_bars': {}
        }
        self._name = "DataStream"
        self._should_run = True
        self._max_frame_size = 2**20
        self._websocket_params = websocket_params or {}

    async def _connect(self) -> None:
        self._ws = await websockets.connect(self._endpoint, **self._websocket_params)
        self._running = True

    async def _auth(self) -> None:
        auth_msg = {
            "action": "authenticate",
            "key_id": self._key_id,
            "secret_key": self._secret_key
        }
        await self._ws.send(json.dumps(auth_msg))

    async def _start_ws(self) -> None:
        await self._connect()
        await self._auth()

    async def close(self) -> None:
        if self._ws:
            await self._ws.close()
            self._running = False

    def stop_ws(self) -> None:
        self._stop_stream_queue.put(True)

    async def _consume(self) -> None:
        while self._should_run:
            try:
                message = await self._ws.recv()
                if self._stop_stream_queue.empty():
                    self._dispatch(json.loads(message))
                else:
                    break
            except websockets.exceptions.ConnectionClosed:
                break

    def _dispatch(self, msg: Dict) -> None:
        msg_type = msg.get('type')
        if msg_type in self._handlers:
            for handler in self._handlers[msg_type].values():
                handler(msg)

    def _subscribe(self, handler: Callable, symbols: List[str], handlers: Dict) -> None:
        for symbol in symbols:
            handlers[symbol] = handler

    def _subscribe_all(self) -> None:
        for data_type, handlers in self._handlers.items():
            for symbol, handler in handlers.items():
                subscription_msg = {
                    "action": "subscribe",
                    "type": data_type,
                    "symbol": symbol
                }
                asyncio.run_coroutine_threadsafe(self._ws.send(json.dumps(subscription_msg)), self._loop)

    async def _run_forever(self) -> None:
        await self._start_ws()
        await self._subscribe_all()
        await self._consume()

    def subscribe_trades(self, handler: Callable, *symbols: str) -> None:
        self._subscribe(handler, list(symbols), self._handlers['trades'])

    def subscribe_quotes(self, handler: Callable, *symbols: str) -> None:
        self._subscribe(handler, list(symbols), self._handlers['quotes'])

    def subscribe_bars(self, handler: Callable, *symbols: str) -> None:
        self._subscribe(handler, list(symbols), self._handlers['bars'])

    def subscribe_updated_bars(self, handler: Callable, *symbols: str) -> None:
        self._subscribe(handler, list(symbols), self._handlers['updated_bars'])

    def subscribe_daily_bars(self, handler: Callable, *symbols: str) -> None:
        self._subscribe(handler, list(symbols), self._handlers['daily_bars'])

    def stop(self) -> None:
        self._should_run = False
        self.stop_ws()
