import json

class Protocol:
    def __init__(self):
        self.next_id = 1
        self.commands = {}
        self.notifications = {}
        self.url = None
        self.on_open = None
        self.on_close = None
        self.socket = None
        self._main_thread_queue = queue.Queue()
        self._main_thread_thread = threading.Thread(target=self._main_thread_loop)
        self._main_thread_thread.daemon = True
        self._main_thread_thread.start()

    def _main_thread_loop(self):
        while True:
            func, args = self._main_thread_queue.get()
            try:
                func(*args)
            except Exception as e:
                print(f"Error in main thread callback: {e}")
            finally:
                self._main_thread_queue.task_done()

    def to_main_thread(self, f, args):
        self._main_thread_queue.put((f, args))

    def connect(self, url, on_open=None, on_close=None):
        self.url = url
        self.on_open = on_open
        self.on_close = on_close
        thread = threading.Thread(target=self.thread_callback)
        thread.start()

    def thread_callback(self):
        self.socket = websocket.WebSocketApp(
            self.url,
            on_open=self.open_callback,
            on_close=self.close_callback,
            on_message=self.message_callback
        )
        self.socket.run_forever()

    def send(self, command, callback=None, options=None):
        cmd_id = self.next_id
        self.next_id += 1
        self.commands[cmd_id] = {'callback': callback, 'options': options}
        message = {
            'id': cmd_id,
            'command': command,
            'options': options
        }
        self.socket.send(json.dumps(message))

    def subscribe(self, notification, callback):
        self.notifications[notification] = callback

    def unsubscribe(self, notification):
        if notification in self.notifications:
            del self.notifications[notification]

    def message_callback(self, ws, message):
        try:
            data = json.loads(message)
            if 'id' in data:
                cmd_id = data['id']
                if cmd_id in self.commands:
                    callback = self.commands[cmd_id]['callback']
                    if callback:
                        self.to_main_thread(callback, (data,))
                    del self.commands[cmd_id]
            elif 'notification' in data:
                notification = data['notification']
                if notification in self.notifications:
                    callback = self.notifications[notification]
                    self.to_main_thread(callback, (data,))
        except Exception as e:
            print(f"Error processing message: {e}")

    def open_callback(self, ws):
        if self.on_open:
            self.to_main_thread(self.on_open, (ws,))

    def close_callback(self, ws):
        if self.on_close:
            self.to_main_thread(self.on_close, (ws,))
