import zmq
import numpy as np

class BertClient:
    def __init__(self, ip='localhost', port=5555, port_out=5556, identity=None, timeout=-1):
        self.context = zmq.Context()
        self.sender = self.context.socket(zmq.PUSH)
        self.receiver = self.context.socket(zmq.SUB)
        self.identity = identity if identity else b'client'
        self.sender.setsockopt(zmq.IDENTITY, self.identity)
        self.receiver.setsockopt(zmq.IDENTITY, self.identity)
        self.sender.connect(f"tcp://{ip}:{port}")
        self.receiver.connect(f"tcp://{ip}:{port_out}")
        self.receiver.setsockopt(zmq.SUBSCRIBE, b'')
        self.request_id = 0
        self.timeout = timeout
        self.pending_request = set()

    def close(self):
        self.sender.close()
        self.receiver.close()
        self.context.term()

    def _send(self, msg, msg_len=0):
        request_id = self.request_id
        self.request_id += 1
        self.pending_request.add(request_id)
        self.sender.send_multipart([str(request_id).encode(), msg])

    def _recv(self):
        if self.timeout > 0:
            poller = zmq.Poller()
            poller.register(self.receiver, zmq.POLLIN)
            socks = dict(poller.poll(self.timeout))
            if socks.get(self.receiver) == zmq.POLLIN:
                return self.receiver.recv_multipart()
            else:
                return None
        else:
            return self.receiver.recv_multipart()

    def _recv_ndarray(self):
        message = self._recv()
        if message is None:
            return None
        array_data = message[1]
        return np.frombuffer(array_data, dtype=np.float32)

    def encode(self, texts, blocking=True):
        self._send(texts)
        if blocking:
            while True:
                message = self._recv()
                if message is not None:
                    request_id = int(message[0])
                    if request_id in self.pending_request:
                        self.pending_request.remove(request_id)
                        return self._recv_ndarray()
        return None

    def fetch(self, delay=0.0):
        import time
        while True:
            time.sleep(delay)
            message = self._recv()
            if message is not None:
                yield message

    def fetch_all(self, sort=True, concat=False):
        responses = []
        for msg in self.fetch():
            responses.append(msg)
        if sort:
            responses.sort(key=lambda x: int(x[0]))
        if concat:
            return np.concatenate(responses)
        return responses
