import pickle

class NetGear:
    def __init__(self, address=None, port=None, protocol=None, pattern=0, receive_mode=False, logging=False, **options):
        self.__logging = logging
        self.__msg_context = zmq.Context()
        self.__receive_mode = receive_mode
        self.__pattern = pattern
        self.__terminate = False
        self.__subscriber_timeout = options.get('subscriber_timeout', 1000) * 1000
        self.__queue = deque()
        self.__thread = None
        self.__msg_socket = None

        if self.__pattern == 0:
            socket_type = zmq.PAIR
        elif self.__pattern == 1:
            socket_type = zmq.REP if receive_mode else zmq.REQ
        elif self.__pattern == 2:
            socket_type = zmq.SUB if receive_mode else zmq.PUB
        else:
            raise ValueError("Invalid pattern")

        self.__msg_socket = self.__msg_context.socket(socket_type)

        if 'compress' in options and options['compress']:
            self.__msg_socket.setsockopt(zmq.COMPRESS, 1)

        if self.__pattern == 2 and self.__receive_mode:
            self.__msg_socket.setsockopt(zmq.SUBSCRIBE, b'')
            self.__msg_socket.setsockopt(zmq.RCVTIMEO, self.__subscriber_timeout)

        if self.__receive_mode:
            if address and port:
                self.__msg_socket.connect(f"{protocol}://{address}:{port}")
        else:
            if address and port:
                self.__msg_socket.bind(f"{protocol}://{address}:{port}")

        if self.__receive_mode:
            self.__thread = threading.Thread(target=self.__recv_handler)
            self.__thread.start()

    def __recv_handler(self):
        while not self.__terminate:
            try:
                parts = []
                while True:
                    try:
                        part = self.__msg_socket.recv(zmq.NOBLOCK)
                        parts.append(part)
                    except zmq.Again:
                        break
                if parts:
                    frame = pickle.loads(parts[-1])
                    self.__queue.append(frame)
            except zmq.Again:
                pass
            except Exception as e:
                if self.__logging:
                    print(f"Error receiving message: {e}")
                break

    def recv(self):
        if not self.__receive_mode:
            raise RuntimeError("recv() can only be called in receive mode")
        return self.__queue.popleft() if self.__queue else None

    def send(self, frame, message=None):
        if self.__receive_mode:
            raise RuntimeError("send() can only be called in send mode")
        if message is not None:
            self.__msg_socket.send(pickle.dumps(message))
        self.__msg_socket.send(pickle.dumps(frame))

    def close(self, kill=False):
        self.__terminate = True
        if self.__thread and self.__thread.is_alive():
            self.__thread.join()
        if self.__msg_socket:
            self.__msg_socket.close()
        if self.__msg_context:
            self.__msg_context.term()
