        with self.lock:
            task_methods = self._get_task_methods(context.invocation_metadata()[0][1])
            result = await self.inference_pipeline.process(request)
            return ModelResponse(**result)

    async def GeneratorReplyStream(self, request: "ModelRequest", context: grpc.ServicerContext) -> Iterator["ModelResponse"]:
        with self.lock:
            task_methods = self._get_task_methods(context.invocation_metadata()[0][1])
            async for chunk in self.inference_pipeline.stream(request):
                yield ModelResponse(**chunk)

    def _get_task_methods(self, method_name: str) -> Dict[str, "TaskMethods"]:
        return self.method_name_to_task.get(method_name, {})

