import httpx
from urllib.parse import urlparse, parse_qs

async def fetch_contributors(auth, GITHUB_API_CONTRIBUTORS, PER_PAGE, GITHUB_USER_TYPE, IGNORED_AUTHOR_LOGINS, ACKNOWLEDGEMENTS):
    async with httpx.AsyncClient(auth=auth) as client:
        contributors = []
        page = 1
        while True:
            response = await client.get(GITHUB_API_CONTRIBUTORS, params={'per_page': PER_PAGE, 'page': page})
            if response.status_code != 200:
                break
            data = response.json()
            if not data:
                break
            contributors.extend(data)
            link_header = response.headers.get('Link', '')
            next_page = None
            if link_header:
                links = link_header.split(',')
                for link in links:
                    if 'rel="next"' in link:
                        next_url = link.split(';')[0].strip('<>')
                        parsed = urlparse(next_url)
                        query_params = parse_qs(parsed.query)
                        next_page = int(query_params.get('page', [1])[0])
                        break
            if next_page is not None:
                page = next_page
            else:
                break
        acknowledged_logins = ACKNOWLEDGEMENTS.split(',') if ACKNOWLEDGEMENTS else []
        filtered = []
        for contrib in contributors:
            if contrib['type'] == GITHUB_USER_TYPE and contrib['login'] not in IGNORED_AUTHOR_LOGINS and contrib['login'] not in acknowledged_logins:
                filtered.append(contrib)
        detailed_users = []
        for contrib in filtered:
            user_response = await client.get(f'https://api.github.com/users/{contrib["login"]}')
            if user_response.status_code == 200:
                detailed_users.append(user_response.json())
        return detailed_users

