from pathlib import Path

class AsyncHttpx:
    proxy: Dict[str, Optional[str]] = {'http://': None, 'https://': None}

    async def get(self, url: Union[str, list[str]], *, params: Optional[Dict[str, Any]] = None, headers: Optional[Dict[str, str]] = None, cookies: Optional[Dict[str, str]] = None, verify: bool = True, use_proxy: bool = True, proxy: Optional[Dict[str, str]] = None, timeout: int = 30, **kwargs) -> Optional[httpx.Response]:
        if isinstance(url, list):
            return await self._get_first_successful(url, params=params, headers=headers, cookies=cookies, verify=verify, use_proxy=use_proxy, proxy=proxy, timeout=timeout, **kwargs)
        else:
            return await self._get_single(url, params=params, headers=headers, cookies=cookies, verify=verify, use_proxy=use_proxy, proxy=proxy, timeout=timeout, **kwargs)

    async def _get_first_successful(self, urls: List[str], **kwargs) -> Optional[httpx.Response]:
        for url in urls:
            response = await self._get_single(url, **kwargs)
            if response:
                return response
        return None

    async def _get_single(self, url: str, *, params: Optional[Dict[str, Any]] = None, headers: Optional[Dict[str, str]] = None, cookies: Optional[Dict[str, str]] = None, verify: bool = True, use_proxy: bool = True, proxy: Optional[Dict[str, str]] = None, timeout: int = 30, **kwargs) -> Optional[httpx.Response]:
        client_proxy = None
        if use_proxy:
            if proxy is not None:
                client_proxy = proxy
            else:
                client_proxy = AsyncHttpx.proxy
        else:
            client_proxy = None

        async with httpx.AsyncClient(proxies=client_proxy, timeout=timeout, verify=verify) as client:
            try:
                response = await client.get(url, params=params, headers=headers, cookies=cookies, **kwargs)
                if response.status_code >= 400:
                    return None
                return response
            except (httpx.ConnectTimeout, httpx.ReadTimeout, httpx.RequestError) as e:
