            if response:
                return await self._download_from_response(response, path)
            else:
                return False

    async def _download_from_response(self, response: httpx.Response, path: Union[str, Path]) -> bool:
        try:
            path_str = str(path)
            with open(path_str, 'wb') as f:
                async for chunk in response.aiter_bytes():
                    f.write(chunk)
            return True
        except Exception as e:
            return False

