import requests
from bs4 import BeautifulSoup

def download_xkcd_comic(issue_number):
    url = f'https://xkcd.com/{issue_number}/'
    response = requests.get(url)
    if response.status_code != 200:
        return f"Error: Unable to fetch comic {issue_number}. Status code: {response.status_code}"
    soup = BeautifulSoup(response.text, 'html.parser')
    img_tag = soup.find('img', id='comic')
    if not img_tag:
        return f"Error: Image not found for comic {issue_number}"
    img_url = img_tag['src']
    if not img_url.startswith('http'):
        img_url = 'https:' + img_url
    img_response = requests.get(img_url, stream=True)
    if img_response.status_code != 200:
        return f"Error: Unable to download image for comic {issue_number}"
    filename = f'xkcd_{issue_number}.png'
    with open(filename, 'wb') as f:
        for chunk in img_response.iter_content(chunk_size=1024):
            if chunk:
                f.write(chunk)
    return f"Success: Image saved as {filename}"

