        self.user_info = {}

    def get_new_token(self) -> str:
        response = requests.post(
            'https://github.com/login/oauth/access_token',
            data={
                'client_id': self.client_id,
                'client_secret': self.client_secret,
                'grant_type': 'refresh_token',
                'refresh_token': self.refresh_token
            },
            headers={'Accept': 'application/json'}
        )
        if response.status_code != 200:
            raise HTTPException(status_code=response.status_code, detail="Failed to get new token")
        data = response.json()
        self.access_token = data['access_token']
        return self.access_token

    def get_user_info(self) -> dict:
        headers = {
            'Authorization': f'token {self.access_token}',
            'Accept': 'application/json'
        }
        user_response = requests.get('https://api.github.com/user', headers=headers)
        if user_response.status_code != 200:
            raise HTTPException(status_code=user_response.status_code, detail="Failed to retrieve user info")
        user_data = user_response.json()
        name = user_data.get('name', '')
        first_name, last_name = (name.split(' ', 1) + [''])[:2]

        emails_response = requests.get('https://api.github.com/user/emails', headers=headers)
        if emails_response.status_code != 200:
            raise HTTPException(status_code=emails_response.status_code, detail="Failed to retrieve emails")
        emails = emails_response.json()
        primary_email = next((email['email'] for email in emails if email.get('primary', False)), None)
        if not primary_email:
            raise HTTPException(status_code=404, detail="No primary email found")

        self.user_info = {
            'email': primary_email,
            'first_name': first_name,
            'last_name': last_name
        }
        return self.user_info

import requests
from urllib.parse import urlencode

class FoursquareOauthClient:
