    ACCESS_TOKEN_URL = "https://foursquare.com/oauth2/access_token"

    def __init__(self, client_id, client_secret, redirect_url):
        self.client_id = client_id
        self.client_secret = client_secret
        self.redirect_url = redirect_url
        self.access_token = None

    def get_authorize_url(self):
        params = {
            'client_id': self.client_id,
            'redirect_uri': self.redirect_url,
            'response_type': 'code'
        }
        return f"{self.AUTHORIZE_URL}?{urlencode(params)}"

    def get_access_token(self, code):
        data = {
            'client_id': self.client_id,
            'client_secret': self.client_secret,
            'code': code,
            'redirect_uri': self.redirect_url
        }
        response = requests.post(self.ACCESS_TOKEN_URL, data=data)
        if response.status_code != 200:
            raise Exception("Failed to retrieve access token")
        token_data = response.json()
        self.access_token = token_data.get('access_token')
        if not self.access_token:
            raise Exception("Access token not found in response")

    def get_user_info(self, api_version='20140806'):
        if not self.access_token:
            raise Exception("No access token available")
        url = f"https://api.foursquare.com/v2/users/self?oauth_token={self.access_token}&v={api_version}"
        response = requests.get(url)
        if response.status_code != 200:
            raise Exception("Failed to retrieve user information")
        user_data = response.json().get('response', {}).get('user', {})
        return user_data

