import requests
from typing import Tuple, Optional, List, Dict

class BlockchainClient:
    def __init__(self):
        self.nodes = set()
        self.chain = None

    def register_node(self, address: str) -> None:
        self.nodes.add(address)

    def resolve_conflicts(self) -> Tuple[bool, Optional[List]]:
        longest_chain = None
        max_length = 0
        for node in self.nodes:
            try:
                response = requests.get(f"{node}/chain")
                if response.status_code == 200:
                    chain = response.json()['chain']
                    length = len(chain)
                    if length > max_length:
                        max_length = length
                        longest_chain = chain
            except:
                pass
        if longest_chain and max_length > len(self.chain):
            self.chain = longest_chain
            return (True, longest_chain)
        return (False, None)

    def get_chain(self, node: str) -> Tuple[bool, Optional[Dict]]:
        try:
            response = requests.get(f"{node}/chain")
            if response.status_code == 200:
                return (True, response.json())
            return (False, None)
        except:
            return (False, None)

