def http_request(method, url, payload_json=None):
    import requests
    try:
        if method.upper() in ['GET']:
            response = requests.get(url)
        elif method.upper() in ['POST']:
            response = requests.post(url, json=payload_json)
        elif method.upper() in ['PUT']:
            response = requests.put(url, json=payload_json)
        elif method.upper() in ['DELETE']:
            response = requests.delete(url)
        else:
            return {'error': 'Unsupported HTTP method'}
        return {
            'status_code': response.status_code,
            'headers': dict(response.headers),
            'body': response.text
        }
    except Exception as e:
        return {'error': str(e)}

