import numpy as np

def get_embedding_from_api(word, model="vicuna-7b-v1.1"):
    if "ada" in model:
        openai_api_key = os.getenv("OPENAI_API_KEY")
        if not openai_api_key:
            print("Error: OPENAI_API_KEY not found in environment variables.")
            return None
        response = requests.post(
            "https://api.openai.com/v1/embeddings",
            headers={"Authorization": f"Bearer {openai_api_key}"},
            json={"input": word, "model": model}
        )
    else:
        response = requests.post(
            "http://localhost:5000/embed",
            json={"text": word, "model": model}
        )

    if response.status_code == 200:
        return np.array(response.json()["embedding"])
    else:
        print(f"Error: {response.status_code} - {response.text}")
        return None

def get_stock_data(symbol):
    import requests
    url = f"https://query1.finance.yahoo.com/v7/finance/quote?symbols={symbol}"
    headers = {"User-Agent": "Mozilla/5.0"}
    response = requests.get(url, headers=headers)
    if response.status_code != 200:
        raise ValueError("HTTP request failed")
    data = response.json()
    quote_response = data.get("quoteResponse", {})
    if not quote_response:
        raise ValueError("No data found for the given symbol")
    result = quote_response.get("result", [{}])[0]
    if not result:
        raise ValueError("No data found for the given symbol")
    return str(result)

