from fastapi import FastAPI, Body
from typing import List, Any, Dict, Union
from pydantic import BaseModel
import uvicorn

class ServeModelInfoResult(BaseModel):
    model_name: str
    model_type: str
    vocab_size: int
    max_sequence_length: int
    special_tokens: Dict[str, int]

class ServeTokenizeResult(BaseModel):
    tokens: List[str]
    token_ids: List[int]
    attention_mask: List[int]

class ServeDeTokenizeResult(BaseModel):
    text: str

class ServeForwardResult(BaseModel):
    outputs: List[Dict[str, Any]]

class ServeCommand:
    def __init__(self, pipeline, host: str = "localhost", port: int = 8888, workers: int = 1) -> None:
        self._pipeline = pipeline
        self.host = host
        self.port = port
        self.workers = workers
        self._app = FastAPI()
        self._setup_routes()

    def _setup_routes(self):
        @self._app.get("/model-info")
        async def model_info():
            return self.model_info()

        @self._app.post("/tokenize")
        async def tokenize(text_input: str = Body(None, embed=True), return_ids: bool = Body(False, embed=True)):
            return self.tokenize(text_input, return_ids)

        @self._app.post("/detokenize")
        async def detokenize(tokens_ids: List[int] = Body(None, embed=True), skip_special_tokens: bool = Body(False, embed=True), cleanup_tokenization_spaces: bool = Body(True, embed=True)):
            return self.detokenize(tokens_ids, skip_special_tokens, cleanup_tokenization_spaces)

        @self._app.post("/forward")
        async def forward(inputs=Body(None, embed=True)):
            return self.forward(inputs)

    def run(self) -> None:
        uvicorn.run(self._app, host=self.host, port=self.port, workers=self.workers)

    def model_info(self) -> ServeModelInfoResult:
        model_name = self._pipeline.model.name_or_path
        model_type = self._pipeline.model.config.model_type
        vocab_size = self._pipeline.tokenizer.vocab_size
        max_sequence_length = self._pipeline.tokenizer.max_length
        special_tokens = self._pipeline.tokenizer.special_tokens_map
        return ServeModelInfoResult(model_name=model_name, model_type=model_type, vocab_size=vocab_size, max_sequence_length=max_sequence_length, special_tokens=special_tokens)

    def tokenize(self, text_input: str = None, return_ids: bool = False) -> ServeTokenizeResult:
        if text_input is None:
            raise ValueError("text_input is required")
        inputs = self._pipeline.tokenizer(text_input, return_tensors="pt", padding=True, truncation=True)
        tokens = self._pipeline.tokenizer.convert_ids_to_tokens(inputs["input_ids"][0].tolist())
        token_ids = inputs["input_ids"][0].tolist()
        attention_mask = inputs["attention_mask"][0].tolist()
        if return_ids:
            return ServeTokenizeResult(tokens=tokens, token_ids=token_ids, attention_mask=attention_mask)
        else:
            return ServeTokenizeResult(tokens=tokens, token_ids=[], attention_mask=attention_mask)

    def detokenize(self, tokens_ids: List[int] = None, skip_special_tokens: bool = False, cleanup_tokenization_spaces: bool = True) -> ServeDeTokenizeResult:
        if tokens_ids is None:
            raise ValueError("tokens_ids is required")
        text = self._pipeline.tokenizer.decode(tokens_ids, skip_special_tokens=skip_special_tokens, clean_up_tokenization_spaces=cleanup_tokenization_spaces)
        return ServeDeTokenizeResult(text=text)

    def forward(self, inputs=None) -> ServeForwardResult:
        if inputs is None:
            raise ValueError("inputs is required")
        outputs = self._pipeline(inputs)
        return ServeForwardResult(outputs=outputs)

