        raise NotImplementedError("predict method must be implemented")

    def query(self, query: Query) -> Answer:
        result = self.predict(query, stream=False)
        return Answer(response=result.get("response", ""), sources=result.get("sources"))

    def produce_chat_answer(self, request: Request, result: Dict[str, Any]) -> Generator[str, None, None]:
        for chunk in result.get("response", "").split(" "):
            yield chunk + " "
        if "sources" in result:
            yield "Sources: " + ", ".join(result["sources"])

    def chat(self, request: Request) -> StreamingResponse:
        data = request.state.data
        if not isinstance(data, dict):
            raise HTTPException(status_code=400, detail="Invalid request data")
        query = Query(**data)
        result = self.predict(query, stream=True)
        return StreamingResponse(self.produce_chat_answer(request, result), media_type="text/plain")

