from fastapi import FastAPI, HTTPException
from pydantic import BaseModel
from typing import Any, Dict

class RequestBody(BaseModel):
    action: str
    parameters: Dict[str, Any]

class ActionServer:
    def __init__(self, action_dispatcher: Any) -> None:
        self._app = FastAPI()
        self._action_dispatcher = action_dispatcher
        self._app.post("/run-action")(self.run_action)
        self._app.get("/actions-list")(self.get_actions_list)

    def run_action(self, body: RequestBody) -> Dict[str, str]:
        if not self._action_dispatcher.has_action(body.action):
            raise HTTPException(status_code=404, detail=f"Action '{body.action}' not found")
        try:
            result = self._action_dispatcher.execute_action(body.action, body.parameters)
            return {"status": "success", "result": result}
        except Exception as e:
            raise HTTPException(status_code=500, detail=str(e))

    def get_actions_list(self) -> Any:
        return self._action_dispatcher.get_actions()

    def get_app(self) -> FastAPI:
        return self._app

