from flask import Flask, request, jsonify
from flask_cors import CORS
from langchain.document_loaders import PyPDFLoader, TextLoader, Docx2txtLoader
from langchain.embeddings import GPT4AllEmbeddings
from langchain.vectorstores import Chroma
from langchain.chains import RetrievalQA
from langchain.llms import GPT4All
import os
from typing import List
from langchain.schema import Document

class FlaskAPI:
    def __init__(self, embeddings_model_name="all-MiniLM-L6-v2", persist_directory="db", model_type="gptj", model_path="models/gpt4all-model.bin", model_n_ctx=512):
        self.app = Flask(__name__)
        CORS(self.app)
        self.embeddings_model_name = embeddings_model_name
        self.persist_directory = persist_directory
        self.model_type = model_type
        self.model_path = model_path
        self.model_n_ctx = model_n_ctx
        self.llm = None
        self._setup_routes()
        self._load_model()

    def _setup_routes(self):
        @self.app.route('/ingest', methods=['GET'])
        def ingest_data():
            source_dir = "docs"
            documents = self._load_documents(source_dir)
            db = Chroma.from_documents(documents, GPT4AllEmbeddings(model_name=self.embeddings_model_name), persist_directory=self.persist_directory)
            db.persist()
            return jsonify({"message": "Documents ingested and stored successfully"})

        @self.app.route('/get_answer', methods=['POST'])
        def get_answer():
            query = request.json.get("query")
            if not query:
                return jsonify({"error": "No query provided"}), 400
            db = Chroma(persist_directory=self.persist_directory, embedding_function=GPT4AllEmbeddings(model_name=self.embeddings_model_name))
            retriever = db.as_retriever()
            chain = RetrievalQA.from_chain_type(llm=self.llm, chain_type="stuff", retriever=retriever, return_source_documents=True)
            result = chain({"query": query})
            return jsonify({"answer": result["result"], "source_documents": [doc.page_content for doc in result["source_documents"]]})

        @self.app.route('/download_model', methods=['GET'])
        def download_and_save():
            model_url = "https://gpt4all.io/models/gpt4all-model.bin"
            os.makedirs(os.path.dirname(self.model_path), exist_ok=True)
            os.system(f"wget {model_url} -O {self.model_path}")
            return jsonify({"message": "Model downloaded and saved successfully"})

    def _load_documents(self, source_dir: str) -> List[Document]:
        documents = []
        for file in os.listdir(source_dir):
            file_path = os.path.join(source_dir, file)
            if file.endswith(".pdf"):
                loader = PyPDFLoader(file_path)
            elif file.endswith(".txt"):
                loader = TextLoader(file_path)
            elif file.endswith(".docx"):
                loader = Docx2txtLoader(file_path)
            else:
                continue
            documents.extend(loader.load())
        return documents

    def _load_single_document(self, file_path: str) -> Document:
        if file_path.endswith(".pdf"):
            loader = PyPDFLoader(file_path)
        elif file_path.endswith(".txt"):
            loader = TextLoader(file_path)
        elif file_path.endswith(".docx"):
            loader = Docx2txtLoader(file_path)
        else:
            raise ValueError("Unsupported file type")
        return loader.load()[0]

    def run(self):
        self.app.run(debug=True)

    def _load_model(self):
        self.llm = GPT4All(model=self.model_path, model_type=self.model_type, n_ctx=self.model_n_ctx)

