import sqlite3
from typing import Union, List, Dict, Tuple, Optional

class FlaskServer:
    def __init__(self) -> None:
        self.app = Flask(__name__)
        self.app.secret_key = 'your_secret_key'
        self.limiter = Limiter(
            app=self.app,
            key_func=get_remote_address,
            default_limits=["200 per day", "50 per hour"]
        )
        self._setup_routes()

    def _setup_routes(self) -> None:
        @self.app.before_request
        def before_request() -> None:
            session.permanent = True
            self.connect_db()

        @self.app.teardown_request
        def teardown_request(exception: Exception) -> None:
            self.app.config['db'].close()

        @self.app.route('/')
        def intmain() -> str:
            papers = self.query_db("SELECT * FROM papers")
            context = self.default_context(papers)
            return render_template('index.html', **context)

        @self.app.route('/login', methods=['POST'])
        def login() -> Response:
            username = request.form.get('username')
            password = request.form.get('password')
            user_id = self.get_user_id(username)
            if user_id and self.check_password(user_id, password):
                session['user_id'] = user_id
                return redirect(url_for('intmain'))
            return "Invalid credentials", 401

    def connect_db(self) -> sqlite3.Connection:
        self.app.config['db'] = sqlite3.connect('database.db')
        return self.app.config['db']

    def query_db(self, query: str, args: Tuple = (), one: bool = False) -> Union[List, Dict, None]:
        db = self.connect_db()
        cur = db.cursor()
        cur.execute(query, args)
        rv = cur.fetchall()
        cur.close()
        if one:
            return rv[0] if rv else None
        return rv

    def get_user_id(self, username: str) -> Optional[int]:
        db = self.connect_db()
        cur = db.cursor()
        cur.execute("SELECT id FROM users WHERE username = ?", (username,))
        user = cur.fetchone()
        cur.close()
        return user[0] if user else None

    def check_password(self, user_id: int, password: str) -> bool:
        db = self.connect_db()
        cur = db.cursor()
        cur.execute("SELECT password FROM users WHERE id = ?", (user_id,))
        stored_password = cur.fetchone()[0]
        cur.close()
        return stored_password == password

    def papers_filter_version(self, papers: List, v: str) -> List:
        return [paper for paper in papers if paper[2] == v]

    def default_context(self, papers: List, **kws) -> Dict:
        return {
            'papers': papers,
            **kws
        }

    def run(self, port: int = 5000, debug: bool = False, prod: bool = False) -> None:
        if prod:
            from gevent.wsgi import WSGIServer
            http_server = WSGIServer(('0.0.0.0', port), self.app)
            http_server.serve_forever()
        else:
            self.app.run(host='0.0.0.0', port=port, debug=debug)

