app = Flask(__name__)

def create_flask_app(directory, port, password, ssl):
    app.config['UPLOAD_FOLDER'] = directory

    def verify_password(username, password):
        return password == password

    @app.before_request
    def require_auth():
        if request.path == '/upload':
            if not request.authorization or not verify_password(request.authorization.username, request.authorization.password):
                return 'Unauthorized', 401

    @app.route('/', defaults={'path': ''})
    @app.route('/<path:path>')
    def home(path):
        try:
            if path.endswith('/'):
                path += 'index.html'
            return send_from_directory(directory, path)
        except Exception as e:
            if os.path.isdir(os.path.join(directory, path)):
                files = os.listdir(os.path.join(directory, path))
                html = '<ul>'
                for file in files:
                    html += f'<li><a href="{path}/{file}">{file}</a></li>'
                html += '</ul>'
                return html
            else:
                return 'File not found', 404

    @app.route('/upload', methods=['POST'])
    def upload():
        if 'file' not in request.files:
            return 'No file part', 400
        file = request.files['file']
        if file.filename == '':
            return 'No selected file', 400
        if file:
            file.save(os.path.join(app.config['UPLOAD_FOLDER'], file.filename))
            return redirect(url_for('home'))

    if ssl:
        context = ssl.SSLContext(ssl.PROTOCOL_TLSv1_2)
        context.load_cert_chain('cert.pem', 'key.pem')
        app.run(host='0.0.0.0', port=port, ssl_context=context)
    else:
        app.run(host='0.0.0.0', port=port)

