        self.app.add_url_rule("/get_logs", "get_logs", self.get_logs, methods=["GET"])

    def _sanitize_report_path_filename(self, report_path_filename):
        try:
            path = Path(report_path_filename).resolve()
            if path.is_file() and path.parent == Path(self.app.config["OWASP_NETTACKER_CONFIG"]["scan_results_dir"]):
                return path
            else:
                return False
        except Exception:
            return False

    def run(self):
        self.app.run(
            host=self.app.config["OWASP_NETTACKER_CONFIG"]["host"],
            port=self.app.config["OWASP_NETTACKER_CONFIG"]["port"],
            ssl_context=(self.app.config["OWASP_NETTACKER_CONFIG"]["ssl_cert"], self.app.config["OWASP_NETTACKER_CONFIG"]["ssl_key"]),
            threaded=True
        )

    def start_api_server(self):
        server_thread = threading.Thread(target=self.run)
        server_thread.daemon = True
        server_thread.start()

    def new_scan(self):
        data = request.get_json()
        if not data or "targets" not in data or "scan_type" not in data:
            return jsonify({"error": "Missing required parameters"}), 400

        targets = data["targets"]
        scan_type = data["scan_type"]
        output_dir = self.app.config["OWASP_NETTACKER_CONFIG"]["scan_results_dir"]

        try:
            command = ["python3", "-m", "owasp_netta", "--targets", ",".join(targets), "--type", scan_type, "--output", output_dir]
            subprocess.Popen(command)
            return jsonify({"message": "Scan initiated successfully"}), 200
        except Exception as e:
            return jsonify({"error": str(e)}), 500

    def get_results_json(self):
        filename = request.args.get("filename")
        if not filename:
            return jsonify({"error": "Filename parameter is required"}), 400

        sanitized_path = self._sanitize_report_path_filename(filename)
        if not sanitized_path:
            return jsonify({"error": "Invalid or unauthorized file access"}), 403

        return send_file(str(sanitized_path), as_attachment=True)

    def get_logs(self):
        filename = request.args.get("filename")
        if not filename:
            return jsonify({"error": "Filename parameter is required"}), 400

        log_dir = self.app.config["OWASP_NETTACKER_CONFIG"]["scan_logs_dir"]
        sanitized_path = Path(log_dir) / filename
        if not sanitized_path.is_file() or not sanitized_path.parent == Path(log_dir):
            return jsonify({"error": "Invalid or unauthorized file access"}), 403

        return send_file(str(sanitized_path), as_attachment=True)

