from flask import Flask, request, jsonify
import json
import os

app = Flask(__name__)
COLORS_FILE = 'colors.json'

if not os.path.exists(COLORS_FILE):
    with open(COLORS_FILE, 'w') as f:
        json.dump([], f)

def load_colors():
    with open(COLORS_FILE, 'r') as f:
        return json.load(f)

def save_colors(colors):
    with open(COLORS_FILE, 'w') as f:
        json.dump(colors, f)

@app.route('/colors', methods=['GET'])
def get_colors():
    colors = load_colors()
    return jsonify(colors)

@app.route('/colors/<name>', methods=['GET'])
def get_color(name):
    colors = load_colors()
    for color in colors:
        if color['name'] == name:
            return jsonify(color)
    return jsonify({'error': 'Color not found'}), 404

@app.route('/colors', methods=['POST'])
def create_color():
    data = request.get_json()
    if not data or 'name' not in data or 'value' not in data:
        return jsonify({'error': 'Invalid input'}), 400
    colors = load_colors()
    for color in colors:
        if color['name'] == data['name']:
            return jsonify({'error': 'Color already exists'}), 400
    colors.append(data)
    save_colors(colors)
    return jsonify(data), 201

@app.route('/colors/<name>', methods=['PUT'])
def update_color(name):
    data = request.get_json()
    if not data or 'value' not in data:
        return jsonify({'error': 'Invalid input'}), 400
    colors = load_colors()
    for color in colors:
        if color['name'] == name:
            color['value'] = data['value']
            save_colors(colors)
            return jsonify(color)
    return jsonify({'error': 'Color not found'}), 404

@app.route('/colors/<name>', methods=['DELETE'])
def delete_color(name):
    colors = load_colors()
    for i, color in enumerate(colors):
        if color['name'] == name:
            deleted_color = colors.pop(i)
            save_colors(colors)
            return jsonify(deleted_color)
    return jsonify({'error': 'Color not found'}), 404

if __name__ == '__main__':
    app.run(debug=True)

