        self.app.route('/api/list/<sid>', methods=['GET'])(self.api_list)
        self.app.route('/api/stop/<sid>', methods=['GET'])(self.api_stop)

    def _start_janitor(self):
        thread = threading.Thread(target=self.janitor, args=(self.sessions,))
        thread.daemon = True
        thread.start()

    def janitor(self, sessions):
        while True:
            time.sleep(60)
            now = time.time()
            for session in list(sessions):
                if now - session['timestamp'] > 3600 or session['completed']:
                    sessions.remove(session)

    def root(self):
        return "DNSTwist API - Use /api/scan to start a scan"

    def api_scan(self):
        data = request.json
        domain = data.get('domain')
        if not domain:
            return jsonify({'error': 'Missing domain parameter'}), 400
        scan_id = len(self.sessions)
        session = {
            'id': scan_id,
            'domain': domain,
            'results': [],
            'completed': False,
            'timestamp': time.time()
        }
        self.sessions.append(session)
        threading.Thread(target=self._run_scan, args=(session,)).start()
        return jsonify({'scan_id': scan_id}), 201

    def _run_scan(self, session):
        try:
            for result in dnstwist.generate(domain=session['domain']):
                session['results'].append(result)
            session['completed'] = True
        except Exception as e:
            session['results'] = [{'error': str(e)}]
            session['completed'] = True

    def api_status(self, sid):
        sid = int(sid)
        for session in self.sessions:
            if session['id'] == sid:
                return jsonify({
                    'status': 'completed' if session['completed'] else 'running',
                    'results_count': len(session['results'])
                }), 200
        return jsonify({'error': 'Session not found'}), 404

    def api_domains(self, sid):
        sid = int(sid)
        for session in self.sessions:
            if session['id'] == sid:
                return jsonify({
                    'domain': session['domain'],
                    'results': session['results']
                }), 200
        return jsonify({'error': 'Session not found'}), 404

    def api_csv(self, sid):
        sid = int(sid)
        for session in self.sessions:
            if session['id'] == sid:
                output = io.StringIO()
                writer = csv.writer(output)
                writer.writerow(['domain', 'ip', 'registrar', 'creation_date', 'expiration_date', 'last_updated'])
                for result in session['results']:
                    writer.writerow([
                        result.get('domain', ''),
                        result.get('ip', ''),
                        result.get('registrar', ''),
                        result.get('creation_date', ''),
                        result.get('expiration_date', ''),
                        result.get('last_updated', '')
                    ])
                return Response(output.getvalue(), mimetype='text/csv', headers={'Content-Disposition': f'attachment;filename=scan_{sid}.csv'}), 200
        return jsonify({'error': 'Session not found'}), 404

    def api_json(self, sid):
        sid = int(sid)
        for session in self.sessions:
            if session['id'] == sid:
                return jsonify({
                    'domain': session['domain'],
                    'results': session['results']
                }), 200
        return jsonify({'error': 'Session not found'}), 404

    def api_list(self, sid):
        sid = int(sid)
        for session in self.sessions:
            if session['id'] == sid:
                output = '\n'.join([result.get('domain', '') for result in session['results']])
                return Response(output, mimetype='text/plain'), 200
        return jsonify({'error': 'Session not found'}), 404

    def api_stop(self, sid):
        sid = int(sid)
        for session in self.sessions:
            if session['id'] == sid:
                session['completed'] = True
                return jsonify({'status': 'stopped'}), 200
        return jsonify({'error': 'Session not found'}), 404

    def run(self, host='127.0.0.1', port=5000):
        self.app.run(host=host, port=port)

