def http_request(method, action, param=None, **params):
    import sys
    import logging
    import urllib.parse
    if sys.version_info[0] < 3:
        import httplib
        from urlparse import urlparse
    else:
        import http.client
        from urllib.parse import urlparse
    logging.basicConfig(level=logging.DEBUG)
    try:
        url = urlparse(action)
        if not url.scheme:
            url = urlparse('http://' + action)
        host = url.hostname
        port = url.port
        if not port:
            port = 80 if url.scheme == 'http' else 443
        proxy = None
        if 'HTTP_PROXY' in os.environ:
            proxy = os.environ['HTTP_PROXY']
        elif 'http_proxy' in os.environ:
            proxy = os.environ['http_proxy']
        if proxy:
            proxy_url = urlparse(proxy)
            proxy_host = proxy_url.hostname
            proxy_port = proxy_url.port or 80
            if sys.version_info[0] < 3:
                conn = httplib.HTTPConnection(proxy_host, proxy_port)
            else:
                conn = http.client.HTTPConnection(proxy_host, proxy_port)
            conn.set_tunnel(host, port)
        else:
            if sys.version_info[0] < 3:
                conn = httplib.HTTPConnection(host, port)
            else:
                conn = http.client.HTTPConnection(host, port)
        if url.scheme == 'https':
            if sys.version_info[0] < 3:
                conn = httplib.HTTPSConnection(host, port)
            else:
                conn = http.client.HTTPSConnection(host, port)
        if param is None:
            param = {}
        param.update(params)
        query = urllib.parse.urlencode(param)
        headers = {'Content-Type': 'application/x-www-form-urlencoded'}
        if method == 'GET':
            conn.request(method, '%s?%s' % (url.path, query), headers=headers)
        elif method == 'POST':
            conn.request(method, url.path, body=query, headers=headers)
        else:
            raise ValueError("Unsupported HTTP method: %s" % method)
        response = conn.getresponse()
        if 200 <= response.status < 300:
            return response.read().decode('utf-8')
        else:
            raise Exception("HTTP request failed with status %d: %s" % (response.status, response.reason))
    except Exception as e:
        logging.error("HTTP request error: %s", e)
        raise
    finally:
        if 'conn' in locals():
