        @self.app.route("/<date>", methods=["GET", "HEAD"])
        @self.app.route("/api/latest", methods=["GET", "HEAD"])
        @self.app.route("/api/<date>", methods=["GET", "HEAD"])
        @cors()
        async def exchange_rates(request, date=None):
            if request.method == "HEAD":
                return json("")

            dt = datetime.now()
            if date:
                try:
                    dt = datetime.strptime(date, "%Y-%m-%d")
                except ValueError as e:
                    return json({"error": "{}".format(e)}, status=400)

                if dt < datetime(1999, 1, 4):
                    return json(
                        {"error": "There is no data for dates older then 1999-01-04."},
