        data = {k: v for k, v in data.items() if v is not None}
        try:
            return json(data, ensure_ascii=False)
        except Exception:
            data = {'success': success, 'message': traceback.format_exc()}
            return json(data, ensure_ascii=False)

    def _error_response(self, message='Invalid request'):
        return self._response(success=False, message=message)

