        return text if text else None

    def _response(self, success: bool = True, data=None, message=None):
        return json({
            'success': success,
            'data': data,
            'message': message
        })

    def _error_response(self, message: str = 'Invalid request'):
        return json({
            'success': False,
            'data': None,
            'message': message
        })

