import socket
import pickle
from typing import Any, Union, Optional, List, Dict, Set

class CSocket:
    CSPROTO = {
        'HEADER': 0,
        'DATA': 1,
        'END': 2
    }

    def __init__(self, sock: str, timeout: int = -1) -> None:
        self.__csock = socket.socket(socket.AF_UNIX, socket.SOCK_STREAM)
        self.__deftout = timeout if timeout != -1 else 60.0
        try:
            self.__csock.connect(sock)
        except Exception as e:
            raise ConnectionError(f"Failed to connect to socket {sock}: {e}")

    def __del__(self) -> None:
        self.close()

    def send(self, msg: Any, nonblocking: bool = False, timeout: Optional[float] = None) -> Any:
        timeout = timeout if timeout is not None else self.__deftout
        if nonblocking:
            self.__csock.setblocking(False)
        else:
            self.__csock.setblocking(True)
        self.__csock.settimeout(timeout)
        try:
            data = pickle.dumps(self.convert(msg))
            self.__csock.sendall(pickle.dumps(CSocket.CSPROTO['HEADER']))
            self.__csock.sendall(pickle.dumps(len(data)))
            self.__csock.sendall(data)
            response = self.receive(self.__csock, nonblocking, timeout)
            return response
        except Exception as e:
            raise RuntimeError(f"Send failed: {e}")

    def settimeout(self, timeout: float) -> None:
        self.__deftout = timeout
        self.__csock.settimeout(timeout)

    def close(self) -> None:
        try:
            self.__csock.shutdown(socket.SHUT_RDWR)
        except Exception:
            pass
        try:
            self.__csock.close()
        except Exception:
            pass

    @staticmethod
    def convert(m: Any) -> Union[str, bool, int, float, list, dict, set]:
        if isinstance(m, (str, bool, int, float)):
            return m
        elif isinstance(m, (list, dict, set)):
            return {k: CSocket.convert(v) for k, v in m.items()} if isinstance(m, dict) else [CSocket.convert(i) for i in m]
        else:
            return str(m)

    @staticmethod
    def receive(sock: socket.socket, nonblocking: bool = False, timeout: Optional[float] = None) -> Any:
        sock.setblocking(not nonblocking)
        sock.settimeout(timeout)
        try:
            header = pickle.loads(sock.recv(pickle.dumps(CSocket.CSPROTO['HEADER']).__len__()))
            if header != CSocket.CSPROTO['HEADER']:
                raise ValueError("Invalid protocol header")
            data_len = pickle.loads(sock.recv(pickle.dumps(len(b''))).__len__())
            data = b''
            while len(data) < data_len:
                packet = sock.recv(data_len - len(data))
                if not packet:
                    break
                data += packet
            return pickle.loads(data)
        except Exception as e:
