        data = b''
        while True:
            char = sock.recv(1)
            if not char:
                return (False, '')
            data += char
            if data.endswith(b'\n'):
                return (True, data.decode('utf-8').rstrip('\n'))

    @staticmethod
    def recvAll(sock: socket.socket, l: str) -> str:
        length = int(l)
        data = b''
        while len(data) < length:
            packet = sock.recv(length - len(data))
            if not packet:
                return ''
            data += packet
        return data.decode('utf-8')

    def run(self) -> None:
