        self.sessions = {}
        self.sequence_numbers = {}
        self.temp_keys = {}

    def run(self):
        while True:
            readable, writable, exceptional = select.select(self.inputs, self.outputs, self.inputs)
            for s in exceptional:
                self.handle_exception(s)
            for s in readable:
                if s is self.sock:
                    self.handle_new_connection(s)
                else:
                    self.handle_message(s)
            for s in writable:
                self.handle_write(s)

    def handle_new_connection(self, sock):
        conn, addr = sock.accept()
        conn.setblocking(False)
        self.inputs.append(conn)
        self.sessions[conn] = {'state': 'idle', 'nonce': None, 'session_key': None}
        self.sequence_numbers[conn] = 0
        self.temp_keys[conn] = None

    def handle_message(self, conn):
        try:
            data = conn.recv(4096)
            if not data:
                self.handle_disconnect(conn)
                return
            msg_type = data[0]
            if msg_type == SESSION_KEY_NEG_START:
                self.handle_sess_key_neg_start(conn, data)
            elif msg_type == SESSION_KEY_NEG_FINISH:
                self.handle_sess_key_neg_finish(conn, data)
            elif msg_type == DP_QUERY_NEW:
                self.handle_dp_query_new(conn, data)
            else:
                self.send_error_response(conn, "Unknown message type")
        except Exception as e:
            self.send_error_response(conn, f"Error processing message: {str(e)}")

    def handle_sess_key_neg_start(self, conn, data):
        nonce = data[1:17]
        self.sessions[conn]['nonce'] = nonce
        response = b'\x01' + nonce
        self.send_message(conn, response)

    def handle_sess_key_neg_finish(self, conn, data):
        encrypted_key = data[1:]
        key = self.decrypt_temp_key(conn, encrypted_key)
        self.sessions[conn]['session_key'] = key
        response = b'\x02' + b'\x00' * 16
        self.send_message(conn, response)

    def decrypt_temp_key(self, conn, encrypted_key):
        temp_key = self.temp_keys[conn]
        cipher = AES.new(temp_key, AES.MODE_ECB)
        decrypted_key = cipher.decrypt(encrypted_key)
        return decrypted_key

    def handle_dp_query_new(self, conn, data):
        seq_num = struct.unpack('<I', data[1:5])[0]
        if seq_num != self.sequence_numbers[conn]:
            self.send_error_response(conn, "Sequence number mismatch")
            return
        self.sequence_numbers[conn] += 1
        response = self.generate_dp_response()
        self.send_message(conn, response)

    def generate_dp_response(self):
        dp_data = b'\x00\x00\x00\x00\x00\x00\x00\x00\x00\x00\x00\x00\x00\x00\x00\x00'
        return b'\x03' + struct.pack('<I', self.sequence_numbers.get(None, 0)) + dp_data

    def send_message(self, conn, message):
        if conn not in self.outputs:
            self.outputs.append(conn)
        conn.sendall(message)

    def send_error_response(self, conn, message):
        error_msg = b'\xff' + message.encode('utf-8')
        self.send_message(conn, error_msg)

    def handle_write(self, conn):
        pass

    def handle_disconnect(self, conn):
        if conn in self.inputs:
            self.inputs.remove(conn)
        if conn in self.outputs:
            self.outputs.remove(conn)
        if conn in self.sessions:
            del self.sessions[conn]
        if conn in self.sequence_numbers:
            del self.sequence_numbers[conn]
        if conn in self.temp_keys:
            del self.temp_keys[conn]
        conn.close()

    def handle_exception(self, conn):
        self.handle_disconnect(conn)

if __name__ == "__main__":
    server = TuyaServer()
    server.run()

