        try:
            if not self._write_data(conn, current_time):
                self._close_connection(conn)
        except (OSError, ValueError):
            self._close_connection(conn)

    def _read_data(self, conn, current_time):
        data = conn.sock.recv(conn.recv_size)
        if not data:
            return False

        conn.in_buffer += data
        conn.last_active = current_time
        return True

    def _write_data(self, conn, current_time):
        num_bytes_sent = conn.sock.send(conn.out_buffer)
