        try:
            self.conn, addr = self.socket.accept()
            self.socket.settimeout(None)
            rfile = BytesIO()
            while True:
                data = self.conn.recv(1024)
                if not data:
                    break
                rfile.write(data)
            rfile.seek(0)
            request = HTTPRequest(rfile)
            if request.parse_request():
                return request
            else:
                request.send_error(400, "Invalid request")
                return request
        except Exception as e:
            request = HTTPRequest(BytesIO())
            request.send_error(500, str(e))
            return request

    def write(self, data):
        if not self.conn:
            raise IOError("No active connection")
        self.conn.sendall(data)

    def close(self, client_only=False):
        if self.conn:
            self.conn.close()
            self.conn = None
        if not client_only and self.socket:
            self.socket.close()
            self.socket = None
            self.bound = False

