import socketserver
import threading

class TCPRequestHandler(socketserver.BaseRequestHandler):
    def get_data(self) -> bytes:
        size_bytes = self.request.recv(2)
        if len(size_bytes) < 2:
            raise ConnectionError("Incomplete size header")
        size = int.from_bytes(size_bytes, byteorder='big')
        data = b''
        while len(data) < size:
            chunk = self.request.recv(size - len(data))
            if not chunk:
                raise ConnectionError("Connection closed before data received")
            data += chunk
        return data

    def send_data(self, data: bytes) -> None:
        size = len(data)
        if size > 65535:
            raise ValueError("Data too large for 2-byte size field")
        size_bytes = size.to_bytes(2, byteorder='big')
        self.request.sendall(size_bytes + data)

    def handle(self) -> None:
        try:
            data = self.get_data()
            # Process the data (example: echo back)
            self.send_data(data)
        except Exception as e:
            print(f"Error handling request: {e}")
